import React, { useState, useEffect, useCallback } from "react"
import spotifyPlaylists from "../services/spotifyPlaylists"
import Grid from "@material-ui/core/Grid"
import { makeStyles, withStyles } from "@material-ui/core/styles"
import { Link } from "react-router-dom"
import Tooltip from "@material-ui/core/Tooltip"
import Typography from "@material-ui/core/Typography"
import CircularProgress from "@material-ui/core/CircularProgress"

import debug from "../helpers/debug"

import LazyLoad from "react-lazyload"

{
    /*
    TODO:
        Browser back button
        Lazyload images, or think of a alternative
*/
}

const useStyles = makeStyles(theme => ({
    imageContainer: {
        position: "relative",
        textAlign: "center"
    },
    textCentered: {
        position: "absolute",
        top: "50%",
        left: "50%",
        width: "calc(100% - 1em)",
        transform: "translate(-50%, -50%)",
        color: "white",
        backgroundColor: "rgba(0,0,0,0.4)"
    },
    image: {
        borderRadius: "5px",
        margin: "0.5em",
        maxWidth: 500,
        maxHeight: 500,
        backgroundColor: "rgba(0,0,0,0)",
        transition: "transform .2s",
        "&:hover": {
            transform: "scale(1.03)"
        }
    }
}))

const HtmlTooltip = withStyles(theme => ({
    tooltip: {
        backgroundColor: "#f5f5f9",
        color: "rgba(0, 0, 0, 0.87)",
        maxWidth: 320,
        fontSize: theme.typography.pxToRem(14),
        border: "1px solid #dadde9"
    }
}))(Tooltip)

const Playlists = ({ accessToken, refreshToken, history, cachedPlaylist }) => {
    const [playlist, setPlaylist] = useState([])

    const classes = useStyles()

    const tokens = {
        accessToken,
        refreshToken
    }

    const currentElement = useCallback(
        node => {
            if (node !== null) {
                if (node.className === history) {
                    console.log("match")
                    node.scrollIntoView({ block: "center", behavior: "smooth" })
                }
            }
        },
        [history]
    )

    useEffect(() => {
        const fetchPlaylists = async () => {
            let response = await spotifyPlaylists.getPlaylists(tokens)
            //console.log(response)
            setPlaylist(response.items)
            //setPlaylist(debug.items)
            //console.log("get playlists")
        }
        if (cachedPlaylist) {
            setPlaylist(cachedPlaylist)
        } else {
            fetchPlaylists()
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <Grid container>
            {playlist.map(list => (
                <Grid
                    container
                    item
                    xs={12}
                    md={6}
                    xl={4}
                    justify="center"
                    key={list.id}
                >
                    <HtmlTooltip
                        title={
                            <div>
                                <Typography variant="h6">
                                    {list.name}
                                </Typography>
                                <Typography>
                                    Tracks: {list.tracks.total}
                                    <br />
                                    Made by: {list.owner.display_name}
                                </Typography>
                            </div>
                        }
                    >
                        <Link
                            to={{
                                pathname: `/main/tracks/`,
                                state: { list, playlist }
                            }}
                        >
                            <div className={`${list.id}`} ref={currentElement}>
                                <div className={classes.imageContainer}>
                                    <LazyLoad
                                        height={500}
                                        placeholder={<CircularProgress />}
                                        once
                                    >
                                        <img
                                            className={classes.image}
                                            src={list.images[0].url}
                                            alt={list.name}
                                        />
                                    </LazyLoad>
                                    <Typography
                                        variant="h6"
                                        className={classes.textCentered}
                                    >
                                        {list.name}
                                    </Typography>
                                </div>
                            </div>
                        </Link>
                    </HtmlTooltip>
                </Grid>
            ))}
        </Grid>
    )
}

export default Playlists
