import React, { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import Button from "@material-ui/core/Button"
import Grid from "@material-ui/core/Grid"
import Typography from "@material-ui/core/Typography"
import spotify from "../services/spotify"
import { CircularProgress } from "@material-ui/core"
import { Redirect } from "react-router"

const Tracks = props => {
    const [tracks, setTracks] = useState(null)
    const [newAccessToken, setNewAccessToken] = useState(
        props.location.state.tokens.accessToken
    )

    useEffect(() => {
        const fetchTracks = async () => {
            //console.log(props.location.state)
            let response = await spotify.getTracks({
                id: props.location.state.list.id,
                accessToken: newAccessToken,
                refreshToken: props.location.state.tokens.refreshToken
            })
            setTracks(response.tracks.items)
            if (response.refreshToken)
                setNewAccessToken(response.refreshedToken)
        }
        fetchTracks()
    }, [newAccessToken, props.location.state.list.id, props.location.state.tokens.refreshToken])

    return (
        <div>
            {props.location.state ? (
                <div>
                    <Grid container spacing={1} justify="center">
                        <Grid item>
                            <Typography variant="h6">
                                {props.location.state.list.name}
                            </Typography>
                        </Grid>
                        <Grid item>
                            <Link
                                to={{
                                    pathname: "/main/",
                                    state: {
                                        history: props.location.state.list.id,
                                        cachedPlaylist:
                                            props.location.state.playlist,
                                        refreshToken:
                                            props.location.state.tokens
                                                .refreshToken,
                                        accessToken: newAccessToken
                                    }
                                }}
                            >
                                <Button variant="contained" color="secondary">
                                    Go back
                                </Button>
                            </Link>
                        </Grid>
                    </Grid>
                    <Grid container>
                        {tracks ? (
                            tracks.map(track => (
                                <Grid
                                    container
                                    item
                                    xs={12}
                                    md={6}
                                    xl={4}
                                    justify="center"
                                    key={track.track.id}
                                >
                                    <Typography>{track.track.name}</Typography>
                                </Grid>
                            ))
                        ) : (
                            <CircularProgress />
                        )}
                    </Grid>
                </div>
            ) : (
                <Redirect to="/main/" />
            )}
        </div>
    )
}

export default Tracks
