import React, { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import Button from "@material-ui/core/Button"
import Grid from "@material-ui/core/Grid"
import Typography from "@material-ui/core/Typography"
import spotify from "../services/spotify"

const Tracks = props => {
    const [tracks, setTracks] = useState(null)

    useEffect(() => {
        const fetchTracks = async () => {
            let response = await spotify.getTracks(props.location.state.list.id)
            console.log(response)
        }
        fetchTracks()
    }, [])

    return (
        <Grid container spacing={1} justify="center">
            <Grid item xs="12">
                <Typography variant="h6">{props.location.state.list.name}</Typography>
            </Grid>
            <Grid item xs="12">
                <Link
                    to={{
                        pathname: "/main/",
                        state: {
                            history: props.location.state.list.id,
                            cachedPlaylist: props.location.state.playlist
                        }
                    }}
                >
                    <Button variant="contained" color="secondary">
                        Go back
                    </Button>
                </Link>
            </Grid>
        </Grid>
    )
}

export default Tracks
