import React, { useState, useEffect, useCallback } from "react"
import spotifyPlaylists from "../services/spotifyPlaylists"
import Grid from "@material-ui/core/Grid"
import Paper from "@material-ui/core/Paper"
import { makeStyles } from "@material-ui/core/styles"
import Container from "@material-ui/core/Container"
import { Link } from "react-router-dom"

import debug from "../helpers/debug"

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        margin: "1em",
        padding: "1em",
        maxWidth: 500,
        transition: "transform .2s",
        '&:hover': {
            transform: "scale(1.03)",
         }
    },
    img: {
        margin: "auto",
        display: "block",
        maxWidth: "100%",
        maxHeight: "100%"
    }
}))

const Playlists = ({ accessToken, refreshToken, history }) => {
    const [playlist, setPlaylist] = useState([])

    const classes = useStyles()

    const tokens = {
        accessToken,
        refreshToken
    }

    const currentElement = useCallback(node => {
        if (node !== null) {
            if (node.className === history) {
                node.scrollIntoView({block: 'center', behavior: 'smooth'})
                console.log("scrolling to " + node.className)
            }
        }
    }, [history])

    useEffect(() => {
        const fetchPlaylists = async () => {
            //let response = await spotifyPlaylists.getPlaylists(tokens)
            //console.log(response)
            //setPlaylist(response.items)
            setPlaylist(debug.items)
        }
        fetchPlaylists()
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <Container maxWidth="lg" className={classes.root}>
            <Grid
                container
                justify="center"
                alignItems="center"
            >
                {playlist.map(list => (
                    <div className={`${list.id}`} style={{backgroundImage: `url(${list.images[0].url})`}} ref={currentElement}>
                        <Link
                            to={{
                                pathname: `/main/tracks/`,
                                state: { list }
                            }}
                        >
                            <Paper className={classes.paper}>
                                <Grid container direction="row">
                                    <Grid item xs={6}>
                                        <img
                                            src={list.images[0].url}
                                            alt={list.name}
                                            className={classes.img}
                                        />
                                    </Grid>
                                    <Grid
                                        item
                                        xs={6}
                                        container
                                        direction="column"
                                    >
                                        <p>{list.name}</p>
                                        <p>Songs: {list.tracks.total}</p>
                                    </Grid>
                                </Grid>
                            </Paper>
                        </Link>
                    </div>
                ))}
            </Grid>
        </Container>
    )
}

export default Playlists
