import React, { useState, useEffect } from "react"
import spotifyPlaylists from "../services/spotifyPlaylists"
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
      },
      paper: {
        padding: theme.spacing(2),
        margin: 'auto',
        maxWidth: 500,
      },
    img: {
        margin: 'auto',
        display: 'block',
        maxWidth: '100%',
        maxHeight: '100%',
      },
}))

const Playlists = ({ accessToken, refreshToken }) => {
    const [playlist, setPlaylist] = useState([])

    const classes = useStyles();
    const tokens = {
        accessToken,
        refreshToken
    }
    useEffect(() => {
        const fetchPlaylists = async () => {
            let response = await spotifyPlaylists.getPlaylists(tokens)
            setPlaylist(response.items)
        }
        fetchPlaylists()
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <div className={classes.root}>
            <Grid container>
                {playlist.map(list => (
                    //<Paper className={classes.paper}>
                        <Grid item xs={12} key={list.key}>
                            <img src={list.images[0].url} alt={list.name} className={classes.img} />
                        </Grid>
                    //</Paper>
                ))}
            </Grid>
        </div>
    )
}

export default Playlists
