import axios from 'axios'
const baseUrl = 'http://localhost:3003/api/spotify'

const getPlaylists = async tokens => {
    const response = await axios.get(baseUrl, tokens)
    console.log(response)
    //return response.data
}

export default {
    getPlaylists
}
