import React, { useState } from "react"
import Modal from "@material-ui/core/Modal"
import Paper from "@material-ui/core/Paper"
import Button from "@material-ui/core/Button"
import Grid from "@material-ui/core/Grid"
import Typography from "@material-ui/core/Typography"
import { makeStyles } from "@material-ui/core/styles"
import { CircularProgress } from "@material-ui/core"

import Beatmaps from "./Beatmaps"

import osu from "../../services/osu"

const useStyles = makeStyles(theme => ({
    paper: {
        padding: "1.5em",
        minWidth: 500
    },
    header: {
        marginTop: "0.5em"
    },
    modal: {
        position: "absolute",
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: "2px solid #000",
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 4),
        transform: "translate(-50%, -50%)",
        top: "50%",
        left: "50%"
    },
    image: {
        maxWidth: 180,
        maxHeight: 180
    },
    textCentered: {
        color: "white",
        backgroundColor: "rgba(0,0,0,0.4)"
    },
    buttonCenter: {
        margin: "auto",
        paddingLeft: "1em"
    }
}))

const TrackList = ({ tracksObj, track }) => {
    const [tracks, setTracks] = useState(tracksObj)
    const [open, setOpen] = useState(false)
    const [loading, setLoading] = useState(null)

    const handleOpen = () => {
        setOpen(true)
    }

    const handleClose = () => {
        setOpen(false)
    }

    const handleSingular = async (track, id) => {
        setLoading(id)
        let copy = [...tracks]
        let response = await osu.getTrack(track, id)
        for (track of copy) {
            if (track.track.id === response.id) {
                let index = copy.findIndex(
                    track => track.track.id === response.id
                )
                let updatedTrack = {
                    ...track,
                    beatmaps: response.osuTracks
                }
                copy[index] = updatedTrack
                setTracks(copy)
                setLoading(null)
            }
        }
    }
    const classes = useStyles()
    return (
        <Grid container item xs={12} justify="center" key={track.track.id}>
            <Paper
                className={classes.paper}
                style={{
                    backgroundImage: `url(${track.track.album.images[0].url})`,
                    backgroundPosition: "center",
                    backgroundSize: "cover",
                    backgroundRepeat: "no-repeat"
                }}
            >
                <Grid item xs={12} sm container>
                    {/*<Grid item>
                                                <img src={track.track.album.images[1].url} className={classes.image}/>
                                            </Grid>*/}
                    <Grid item xs container direction="column" spacing={2}>
                        <Grid item xs className={classes.textCentered}>
                            <Typography gutterBottom variant="h6">
                                {track.track.artists[0].name}
                            </Typography>
                            <Typography gutterBottom variant="subtitle1">
                                {track.track.name}
                            </Typography>
                            <Typography variant="body2" color="textSecondary">
                                ID: {track.track.id}
                            </Typography>
                        </Grid>
                    </Grid>
                    <Grid item className={classes.buttonCenter}>
                        {track.beatmaps ? (
                            <div>
                                <Button
                                    variant="contained"
                                    color="primary"
                                    onClick={handleOpen}
                                >
                                    Maps
                                </Button>
                                <Modal
                                    aria-labelledby="simple-modal-title"
                                    aria-describedby="simple-modal-description"
                                    open={open}
                                    onClose={handleClose}
                                >
                                    <Beatmaps beatmaps={track.beatmaps}/>
                                </Modal>
                            </div>
                        ) : loading !== track.track.id ? (
                            <Button
                                variant="contained"
                                color="primary"
                                onClick={() =>
                                    handleSingular(track.track, track.track.id)
                                }
                            >
                                Get
                            </Button>
                        ) : (
                            <CircularProgress />
                        )}
                    </Grid>
                </Grid>
            </Paper>
        </Grid>
    )
}

export default TrackList
