import React, { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import Button from "@material-ui/core/Button"
import Grid from "@material-ui/core/Grid"
import Typography from "@material-ui/core/Typography"
import spotify from "../services/spotify"
import { CircularProgress } from "@material-ui/core"
import { Redirect } from "react-router"
import Paper from "@material-ui/core/Paper"
import { makeStyles } from "@material-ui/core/styles"

const useStyles = makeStyles(theme => ({
    paper: {
        padding: "1.5em",
        maxWidth: 500
    }
}))

const Tracks = props => {
    const [tracks, setTracks] = useState(null)
    const [newAccessToken, setNewAccessToken] = useState(
        props.location.state.tokens.accessToken
    )

    const classes = useStyles()

    useEffect(() => {
        const fetchTracks = async () => {
            //console.log(props.location.state)
            let response = await spotify.getTracks({
                id: props.location.state.list.id,
                accessToken: newAccessToken,
                refreshToken: props.location.state.tokens.refreshToken
            })
            setTracks(response.tracks.items)
            if (response.refreshToken)
                setNewAccessToken(response.refreshedToken)
        }
        fetchTracks()
    }, [
        newAccessToken,
        props.location.state.list.id,
        props.location.state.tokens.refreshToken
    ])

    return (
        <div>
            {props.location.state ? (
                <div>
                    <Typography variant="h6">
                        {props.location.state.list.name}
                    </Typography>

                    <Link
                        to={{
                            pathname: "/main/",
                            state: {
                                history: props.location.state.list.id,
                                cachedPlaylist: props.location.state.playlist,
                                refreshToken:
                                    props.location.state.tokens.refreshToken,
                                accessToken: newAccessToken
                            }
                        }}
                    >
                        <Button variant="contained" color="secondary">
                            Go back
                        </Button>
                    </Link>

                    <Grid container spacing={2}>
                        {tracks ? (
                            tracks.map(track => (
                                <Grid
                                    container
                                    item
                                    xs={12}
                                    md={6}
                                    xl={4}
                                    justify="center"
                                    key={track.track.id}
                                >
                                    <Paper className={classes.paper}>
                                        <Grid item xs={12} sm container>
                                            <Grid
                                                item
                                                xs
                                                container
                                                direction="column"
                                                spacing={2}
                                            >
                                                <Grid item xs>
                                                    <Typography
                                                        gutterBottom
                                                        variant="subtitle1"
                                                    >
                                                        {track.track.name}
                                                    </Typography>
                                                    <Typography
                                                        variant="body2"
                                                        gutterBottom
                                                    >
                                                        Lorem
                                                    </Typography>
                                                    <Typography
                                                        variant="body2"
                                                        color="textSecondary"
                                                    >
                                                        ID: {track.track.id}
                                                    </Typography>
                                                </Grid>
                                            </Grid>
                                            <Grid item>
                                                <Button variant="contained" color="primary">
                                                    Download
                                                </Button>
                                            </Grid>
                                        </Grid>
                                    </Paper>
                                </Grid>
                            ))
                        ) : (
                            <CircularProgress />
                        )}
                    </Grid>
                </div>
            ) : (
                <Redirect to="/main/" />
            )}
        </div>
    )
}

export default Tracks
