import React, { useState, useEffect } from "react"
import spotifyPlaylists from "../services/spotifyPlaylists"
import GridList from "@material-ui/core/GridList"
import GridListTile from "@material-ui/core/GridListTile"

const Playlists = ({ accessToken, refreshToken }) => {
    const [playlist, setPlaylist] = useState([])

    const tokens = {
        accessToken,
        refreshToken
    }
    useEffect(() => {
        const fetchPlaylists = async () => {
            let response = await spotifyPlaylists.getPlaylists(tokens)
            setPlaylist(response.items)
        }
        fetchPlaylists()
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <div>
            <GridList>
                {playlist.map(list => (
                    <GridListTile key={list.key} cellHeight={1240}>
                        <img src={list.images[0].url} alt={list.name} />
                    </GridListTile>
                ))}
            </GridList>
        </div>
    )
}

export default Playlists
