import React, { useState } from "react"
import "./App.css"
import axios from "axios"

import Main from './components/Main'

const App = () => {
    const [token, setToken] = useState(false)

    const redirectUrl = "http://localhost:3000/main"
    const authUrl =
        "https://accounts.spotify.com/authorize?client_id=aef42b48cc74441299b7b1ac9b42a779&response_type=code&redirect_uri=" +
        redirectUrl

    const Login = () => {
        return (
            <div>
              <h1>SpoOsu!</h1>
                <button href={authUrl}>Login via Spotify</button>
            </div>
        )
    }

    return <div className="App">{token ? <Login /> : <Main />}</div>
}

export default App
