import React, { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import Button from "@material-ui/core/Button"
import Grid from "@material-ui/core/Grid"
import Typography from "@material-ui/core/Typography"
import { CircularProgress } from "@material-ui/core"
import { Redirect } from "react-router"
import Paper from "@material-ui/core/Paper"
import { makeStyles } from "@material-ui/core/styles"
import Modal from "@material-ui/core/Modal"

import osu from "../services/osu"
import spotify from "../services/spotify"

const useStyles = makeStyles(theme => ({
    paper: {
        padding: "1.5em",
        minWidth: 500
    },
    modal: {
        position: "absolute",
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: "2px solid #000",
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 4),
        transform: "translate(-50%, -50%)",
        top: "50%",
        left: "50%"
    },
    image: {
        maxWidth: 180,
        maxHeight: 180
    },
    textCentered: {
        top: "50%",
        left: "50%",


        color: "white",
        backgroundColor: "rgba(0,0,0,0.4)"
    },
}))

const Tracks = props => {
    const [tracks, setTracks] = useState(null)
    const [newAccessToken, setNewAccessToken] = useState(
        props.location.state.tokens.accessToken
    )
    const [open, setOpen] = React.useState(false)

    const classes = useStyles()

    const handleOpen = () => {
        setOpen(true)
    }

    const handleClose = () => {
        setOpen(false)
    }

    const handleSingular = async (track, id) => {
        let copy = [...tracks]
        let response = await osu.getTrack(track, id)
        for (track of copy) {
            if (track.track.id === response.id) {
                let index = copy.findIndex(
                    track => track.track.id === response.id
                )
                let updatedTrack = {
                    ...track,
                    beatmaps: response.osuTracks
                }
                copy[index] = updatedTrack
                setTracks(copy)
            }
        }
    }

    useEffect(() => {
        const fetchTracks = async () => {
            let response = await spotify.getTracks({
                id: props.location.state.list.id,
                accessToken: newAccessToken,
                refreshToken: props.location.state.tokens.refreshToken
            })
            setTracks(response.tracks.items)
            if (response.refreshedToken)
                setNewAccessToken(response.refreshedToken)
        }
        fetchTracks()
    }, [
        newAccessToken,
        props.location.state.list.id,
        props.location.state.tokens.refreshToken
    ])

    console.log(tracks)
    return (
        <div>
            {props.location.state ? (
                <div>
                    <Typography variant="h6">
                        {props.location.state.list.name}
                    </Typography>

                    <Link
                        to={{
                            pathname: "/main/",
                            state: {
                                history: props.location.state.list.id,
                                cachedPlaylist: props.location.state.playlist,
                                refreshToken:
                                    props.location.state.tokens.refreshToken,
                                accessToken: newAccessToken
                            }
                        }}
                    >
                        <Button variant="contained" color="secondary">
                            Go back
                        </Button>
                    </Link>

                    <Grid container spacing={2}>
                        {tracks ? (
                            tracks.map(track => (
                                <Grid
                                    container
                                    item
                                    xs={12}
                                    md={6}
                                    xl={4}
                                    justify="center"
                                    key={track.track.id}
                                >
                                    <Paper className={classes.paper} style={{ backgroundImage: `url(${track.track.album.images[0].url})` }}>
                                        <Grid item xs={12} sm container>
                                            {/*<Grid item>
                                                <img src={track.track.album.images[1].url} className={classes.image}/>
                                            </Grid>*/}
                                            <Grid
                                                item
                                                xs
                                                container
                                                direction="column"
                                                spacing={2}
                                            >
                                                <Grid item xs className={classes.textCentered}>
                                                <Typography
                                                        gutterBottom
                                                        variant="h6"
                                                    >
                                                        {track.track.artists[0].name}
                                                    </Typography>
                                                    <Typography
                                                        gutterBottom
                                                        variant="subtitle1"
                                                    >
                                                        {track.track.name}
                                                    </Typography>
                                                    <Typography
                                                        variant="body2"
                                                        color="textSecondary"
                                                    >
                                                        ID: {track.track.id}
                                                    </Typography>
                                                </Grid>
                                            </Grid>
                                            <Grid item>
                                                {track.beatmaps ? (
                                                    <div>
                                                        <Button
                                                            variant="contained"
                                                            color="primary"
                                                            onClick={handleOpen}
                                                        >
                                                            Beatmaps
                                                        </Button>
                                                        <Modal
                                                            aria-labelledby="simple-modal-title"
                                                            aria-describedby="simple-modal-description"
                                                            open={open}
                                                            onClose={
                                                                handleClose
                                                            }
                                                        >
                                                            <div
                                                                className={
                                                                    classes.modal
                                                                }
                                                            >
                                                                <h2 id="simple-modal-title">
                                                                    Beatmaps
                                                                </h2>
                                                                <div id="simple-modal-description">
                                                                    {track.beatmaps.beatmaps.map(
                                                                        beatmap => (
                                                                        <div key={beatmap.beatmap_id}>
                                                                            <Typography>
                                                                                {beatmap.title}, by {beatmap.mapper} AR {beatmap.difficulty_ar}
                                                                            </Typography>
                                                                            <Button variant="contained" color="primary" target="_blank" href={'https://osu.ppy.sh/beatmapsets/' + beatmap.beatmapset_id}>
                                                                                Download
                                                                            </Button>
                                                                        </div>
                                                                        )
                                                                    )}
                                                                </div>
                                                            </div>
                                                        </Modal>
                                                    </div>
                                                ) : (
                                                    <Button
                                                        variant="contained"
                                                        color="primary"
                                                        onClick={() =>
                                                            handleSingular(
                                                                track.track,
                                                                track.track.id
                                                            )
                                                        }
                                                    >
                                                        Get
                                                    </Button>
                                                )}
                                            </Grid>
                                        </Grid>
                                    </Paper>
                                </Grid>
                            ))
                        ) : (
                            <CircularProgress />
                        )}
                    </Grid>
                </div>
            ) : (
                <Redirect to="/main/" />
            )}
        </div>
    )
}

export default Tracks
