import React, { useState, useEffect, useCallback } from "react"
import spotifyPlaylists from "../services/spotifyPlaylists"
import Grid from "@material-ui/core/Grid"
import Paper from "@material-ui/core/Paper"
import { makeStyles } from "@material-ui/core/styles"
import Container from "@material-ui/core/Container"
import { Link } from "react-router-dom"

import debug from "../helpers/debug"

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1
    },
    paper: {
        padding: "2em",
        margin: "2em",
        maxWidth: 500
    },
    img: {
        margin: "auto",
        display: "block",
        maxWidth: "100%",
        maxHeight: "100%"
    }
}))

const Playlists = ({ accessToken, refreshToken, history }) => {
    const [playlist, setPlaylist] = useState([])

    const classes = useStyles()

    const tokens = {
        accessToken,
        refreshToken
    }

    const currentElement = useCallback(node => {
        if (node !== null) {
          if (node.props.className === history) {
              console.log(node.props)
              node.props.scrollIntoView({ 
                behavior: "smooth", 
                block: "nearest"
             })
              console.log('MATCH')
          }
        }
      }, []);

    useEffect(() => {
        const fetchPlaylists = async () => {
            //let response = await spotifyPlaylists.getPlaylists(tokens)
            //console.log(response)
            //setPlaylist(response.items)
            setPlaylist(debug.items)
        }
        fetchPlaylists()
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <Container maxWidth="lg" className={classes.root}>
            <Grid
                container
                direction="column"
                justify="center"
                alignItems="center"
            >
                {playlist.map(list => (
                    <Link
                        to={{
                            pathname: `/main/tracks/${list.id}`,
                            state: { list }
                        }}
                        className={`${list.id}`}
                        ref={currentElement}
                    >
                        <Paper className={classes.paper}>
                            <Grid container direction="row">
                                <Grid item xs={6}>
                                    <img
                                        src={list.images[0].url}
                                        alt={list.name}
                                        className={classes.img}
                                    />
                                </Grid>
                                <Grid item xs={6} container direction="column">
                                    <p>{list.name}</p>
                                    <p>Songs: {list.tracks.total}</p>
                                </Grid>
                            </Grid>
                        </Paper>
                    </Link>
                ))}
            </Grid>
        </Container>
    )
}

export default Playlists
