import React, { useState, useEffect, useCallback } from "react"
import spotify from "../services/spotify"
import Grid from "@material-ui/core/Grid"
import { makeStyles, withStyles } from "@material-ui/core/styles"
import { Link } from "react-router-dom"
import Tooltip from "@material-ui/core/Tooltip"
import Typography from "@material-ui/core/Typography"
import CircularProgress from "@material-ui/core/CircularProgress"

import debug from "../helpers/debug"

import LazyLoad from "react-lazyload"


    /*
    TODO:
        Browser back button
    */


const useStyles = makeStyles(theme => ({
    imageContainer: {
        position: "relative",
        textAlign: "center",
        transition: "transform .2s",
        "&:hover": {
            transform: "scale(1.03)"
        }
    },
    textCentered: {
        position: "absolute",
        top: "50%",
        left: "50%",
        width: "calc(96%)",
        transform: "translate(-50%, -50%)",
        color: "white",
        backgroundColor: "rgba(0,0,0,0.4)"
    },
    image: {
        borderRadius: "5px",
        margin: "0.5em",
        maxWidth: 500,
        maxHeight: 500,
        backgroundColor: "rgba(0,0,0,0)",
    },
    placeholder: {
        minWidth: 500,
        minHeight: 500,
    },
}))

const HtmlTooltip = withStyles(theme => ({
    tooltip: {
        backgroundColor: "#f5f5f9",
        color: "rgba(0, 0, 0, 0.87)",
        maxWidth: 320,
        fontSize: theme.typography.pxToRem(14),
        border: "1px solid #dadde9"
    }
}))(Tooltip)

const Playlists = ({ accessToken, refreshToken, history, cachedPlaylist }) => {
    const [playlist, setPlaylist] = useState(null)
    const [newAccessToken, setNewAccessToken] = useState(accessToken)

    const classes = useStyles()

    const tokens = {
        accessToken: newAccessToken,
        refreshToken
    }

    const currentElement = useCallback(
        node => {
            if (node !== null) {
                if (node.className === history) {
                    node.scrollIntoView()
                }
            }
        },
        [history]
    )

    useEffect(() => {
        const fetchPlaylists = async () => {
            let response = await spotify.getPlaylists(tokens)
            //console.log(response)
            setPlaylist(response.playlists.items)
            //setPlaylist(debug.items)
            //console.log("get playlists")
            setNewAccessToken(response.refreshedToken)
        }
        if (cachedPlaylist) {
            setPlaylist(cachedPlaylist)
        } else {
            fetchPlaylists()
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <Grid container>
            {playlist ? playlist.map(list => (
                <Grid
                    container
                    item
                    xs={12}
                    md={6}
                    xl={4}
                    justify="center"
                    key={list.id}
                >
                    <div className={`${list.id}`} ref={currentElement}>
                        <HtmlTooltip
                            title={
                                <div>
                                    <Typography variant="h6">
                                        {list.name}
                                    </Typography>
                                    <Typography>
                                        Tracks: {list.tracks.total}
                                        <br />
                                        Made by: {list.owner.display_name}
                                    </Typography>
                                </div>
                            }
                        >
                            <Link
                                to={{
                                    pathname: `/main/tracks/`,
                                    state: { list, playlist, tokens}
                                }}
                            >
                                <div className={classes.imageContainer}>
                                    <LazyLoad
                                        placeholder={
                                            <div
                                                className={classes.placeholder}
                                            />
                                        }
                                        once
                                        throttle={1000}
                                    >
                                        <img
                                            className={classes.image}
                                            src={list.images[0].url}
                                            alt={list.name}
                                        />
                                    </LazyLoad>
                                    <Typography
                                        variant="h6"
                                        className={classes.textCentered}
                                    >
                                        {list.name}
                                    </Typography>
                                </div>
                            </Link>
                        </HtmlTooltip>
                    </div>
                </Grid>
            )) : 
            <Grid container item justify="center">
                <CircularProgress/>
            </Grid>
            }
            
        </Grid>
    )
}

export default Playlists
